INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '先日', 'せんじつ', 'hôm nọ, hôm trước', 'Ａ（えい）「先日（せんじつ）は、どうもありがとございました。」
