INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '伸びる', 'のびる', 'Phát triển', '弟（おとうと）は高校生（こうこうせい）で、まだ身長（しんちょう）が伸（の）びている。', 'Em trai là học sinh cấp 3 nên vẫn còn phát triển chiều cao.',36, '/Sound/N3/0873.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '測る', 'はかる', 'Đo', '身長（しんちょう）を測（はか）ったら、1年（ねん）前（まえ）より2センチ伸（の）びていた。', 'Khi đo chiều cao thì nhận ra đã cao hơn 2cm so với 1 năm trước.',36, '/Sound/N3/0874.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '体重', 'たいじゅう', 'Cân nặng, thể trọng', '朝（あさ）と夜（よる）、体重（たいじゅう）をチェックしている。', 'Tôi kiểm tra cân nặng vào buổi sáng và buổi tối.',36, '/Sound/N3/0875.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '体重計', 'たいじゅうけい', 'Cân, cân sức khỏe', '新（あたら）しい体重（たいじゅう）計（けい）で、家族（かぞく）の健康（けんこう）をチェックする。', 'Tôi kiểm tra sức khỏe của gia đình bằng cân sức khỏe mới.',36, '/Sound/N3/0876.mp3');
