INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '額', 'ひたい', 'Trán', '彼（かれ）の額（ひたい）をさわったら、とても熱（あつ）かった。', 'Sờ trán cậu ta thấy rất nóng.',36, '/Sound/N3/0878.mp3');
