INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '心臓', 'しんぞう', 'Tim', '運動（うんどう）すると、心臓（しんぞう）の動（うご）きが速（はや）くなる。', 'Khi vận động, tim sẽ đập nhanh.',36, '/Sound/N3/0881.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '汗', 'あせ', 'Mồ hôi', 'スポーツで汗（あせ）をかくのは気持（きも）ちがいい。', 'Việc ra mồ hôi khi tập thể thao rất dễ chịu.',36, '/Sound/N3/0882.mp3');
