INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '皮ふ', 'ひふ', 'Da', '皮（ひ）ふが弱（よわ）いので、クリームを使（つか）っている。', 'Da yếu nên tôi dùng kem bôi.',36, '/Sound/N3/0885.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '顔色', 'かおいろ', 'Sắc mặt', '今日（きょう）、彼（かれ）は顔色（かおいろ）が悪（わる）い。', 'Hôm nay, sắc mặt anh ta trông xấu.',36, '/Sound/N3/0886.mp3');
