INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '虫歯', 'むしば', 'Răng sâu', '虫歯（むしば）が見（み）つかったので、歯医者（はいしゃ）に通（かよ）っている。', 'Phát hiện ra răng sâu nên tôi đang đi đến bác sỹ răng để điều trị.',36, '/Sound/N3/0891.mp3');
