INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '気になる', 'きになる', 'Bận tâm', '最近（さいきん）、健康（けんこう）のことが気（き）になっている。', 'Gần đây tôi bận tâm đến vấn đề sức khỏe.',37, '/Sound/N3/0899.mp3');
