INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '傷', 'きず', 'Vết thương', '転（ころ）んで、ひざに傷（きず）ができた。', 'Tôi ngã nên bị vết thương ở đầu gối.',37, '/Sound/N3/0907.mp3');
