INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '酔っぱらい', 'よっぱらい', 'Kẻ say rượu', '忘年会（ぼうねんかい）の季節（きせつ）は、酔（よ）っぱらいが増（ふ）える。', 'Mùa liên hoan cuối năm, những kẻ say rượu tăng lên.',37, '/Sound/N3/0909.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ぺこぺこな', 'Không có Furigana', 'Cồn cào', 'ジョギングのあとなので、おなかがぺこぺこだ。', 'Vừa chạy bộ xong nên bụng đói cồn cào.',37, '/Sound/N3/0910.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'からからな', 'Không có Furigana', 'Khát khô', '今日（きょう）は暑（あつ）くて、のどがからからになった。', 'Hôm nay trời nóng nên cổ họng khát khô.',37, '/Sound/N3/0911.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ダイエット', 'Không có Furigana', 'Ăn kiêng', '太（ふと）ったので、ダイエットを始（はじ）めようと思（おも）う。', 'Tôi béo nên định bắt đầu ăn kiêng.',37, '/Sound/N3/0912.mp3');
