INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'つい', 'Không có Furigana', 'Biết thế mà cứ', '食事（しょくじ）のあとに、つい甘（あま）い物（もの）を食（た）べてしまう。', 'Biết thế mà sau bữa ăn cứ ăn đồ ngọt.',37, '/Sound/N3/0915.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'アレルギー', 'Không có Furigana', 'Dị ứng', '病院（びょういん）で、猫（ねこ）アレルギーだと言（い）われた。', 'Ở bệnh viện người ta nói tôi bị dị ứng mèo.',38, '/Sound/N3/0916.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '花粉症', 'かふんしょう', 'Dị ứng phấn hoa', 'くしゃみが止（と）まらない。花粉（かふん）症（しょう）かもしれない。', 'Bị hắt xì hơi mãi không dứt. Có lẽ tôi bị dị ứng phấn hoa.',38, '/Sound/N3/0917.mp3');
