INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'マスク', 'Không có Furigana', 'Khẩu trang', '花粉（かふん）症（しょう）の季節（きせつ）は、マスクをする。', 'Mùa dị ứng phấn hoa tôi đeo khẩu trang.',38, '/Sound/N3/0922.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'つらい', 'Không có Furigana', 'Mệt mỏi, khó chịu', '今朝（けさ）から熱（ねつ）があって、つらい。', 'Từ sáng đến giờ sốt, khó chịu.',38, '/Sound/N3/0923.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'かゆい', 'Không có Furigana', 'Ngứa', '花粉（かふん）が多（おお）い日（ひ）は、目（め）がかゆくなる。', 'Những ngày nhiều phấn hoa, mắt bị ngứa.',38, '/Sound/N3/0924.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'かゆみ', 'Không có Furigana', 'Sự ngứa ngáy', 'かゆみが、だんだんひどくなってきた。', 'Sự ngứa ngáy dần dần nặng lên.',38, '/Sound/N3/0925.mp3');
