INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '丘', 'おか', 'Đồi', '向（む）こうの丘（おか）の上（うえ）に、小（ちい）さな家（いえ）が見（み）える。', 'Tôi nhìn thấy một ngôi nhà nhỏ nằm trên ngọn đồi đối diện.',41, '/Sound/N3/0992.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '火山', 'かざん', 'Núi lửa', '日本（にほん）には、火山（かざん）がたくさんある。', 'Ở Nhật Bản có nhiều núi lửa.',41, '/Sound/N3/0993.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '想像＜する＞', 'そうぞう＜する＞', 'Sự tưởng tượng', 'ここが昔（むかし）は海（うみ）だったなんて、想像（そうぞう）できない。', 'Tôi không thể tưởng tượng được ở đây ngày xưa là biển.',41, '/Sound/N3/0994.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '見上げる', 'みあげる', 'Nhìn lên', '空（そら）を見上（みあ）げると、星（ほし）がいっぱいだった。', 'Nhìn bầu trời thấy trời đầy sao.',41, '/Sound/N3/0995.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '見下ろす', 'みおろす', 'Nhìn xuống', '山の上（やまのうえ）から、町（まち）を見下（みお）ろす。', 'Từ đỉnh núi tôi nhìn xuống thành phố.',41, '/Sound/N3/0996.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ほえる', 'Không có Furigana', 'Sủa, kêu', '夜（よる）、動物（どうぶつ）がほえるのが聞（き）こえた。', 'Ban đêm, nghe thấy tiếng thú kêu.',41, '/Sound/N3/0997.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'しっぽ', 'Không có Furigana', 'Đuôi', 'うちの猫（ねこ）のしっぽは長（なが）い。', 'Đuôi con mèo nhà tôi dài.',41, '/Sound/N3/0998.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'さびる', 'Không có Furigana', 'Han gỉ', '海（うみ）のそばに引っ越（ひっこ）したら、車（くるま）がさびた。', 'Tôi chuyển nhà đến cạnh biển, thế là ô tô bị han gỉ.',41, '/Sound/N3/0999.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '予想＜する＞', 'よそう＜する＞', 'Dự đoán', '明日（あした）は雨（あめ）だと予想（よそう）している。', 'Dự đoán, ngày mai trời mưa.',42, '/Sound/N3/1000.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '予報＜する＞', 'よほう＜する＞', 'Dự báo', '明日（あした）の予報（よほう）は大雨（おおあめ）だ。', 'Dự báo ngày mai trời mưa to.',42, '/Sound/N3/1001.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '湿度', 'しつど', 'Độ ẩm', '今日（きょう）は、とても湿度（しつど）が高（たか）くなりそうだ。', 'Hôm nay độ ẩm có lẽ sẽ cao.',42, '/Sound/N3/1002.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '湿気', 'しっけ', 'Hơi ẩm, độ ẩm, ẩm ướt', '日本（にほん）の夏（なつ）は湿気（しっけ）が多（おお）い。', 'Mùa hè Nhật Bản nhiều hơi ẩm.',42, '/Sound/N3/1003.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'くもる', 'Không có Furigana', 'Mây mù, trời mây', '今（いま）はくもっているが、すぐに晴（は）れるだろう。', 'Bây giờ trời đang mây nhưng có lẽ sẽ ngắng ngay thôi.',42, '/Sound/N3/1004.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '嵐', 'あらし', 'Bão, giông tố', '嵐（あらし）になりそうなので、早（はや）めに家（いえ）に帰（かえ）ろう。', 'Có lẽ sẽ có bão nên nhanh chóng về nhà thôi!',42, '/Sound/N3/1005.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '強風', 'きょうふう', 'gió to', '強風（きょうふう）で木（き）が倒（たお）れた。', 'Gió to làm đổ cây.',42, '/Sound/N3/1006.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '大雨', 'おおあめ', 'Mưa to', '昨日（きのう）は大雨（おおあめ）で出（で）かけられなかった。', 'Hôm qua mưa to nên tôi không thể đi ra ngoài được.',42, '/Sound/N3/1007.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '折りたたみ傘', 'おりたたみがさ', 'Ô gấp, dù xếp', '折（お）りたたみ傘（がさ）をバッグに入（い）れて、出（で）かける。', 'Tôi bỏ chiếc ô gấp vào túi xách và đi ra ngoài',42, '/Sound/N3/1008.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '（傘を）さす', '（かさを）さす', 'Che (ô)', '雨（あめ）の中（なか）、傘（かさ）をさしていない人（ひと）もいる。', 'Trong mưa cũng có người không che ô.',42, '/Sound/N3/1009.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'にわか雨', 'にわかあめ', 'Mưa rào', 'にわか雨が降（お）りそうなので、傘（かさ）を持（も）って行（い）く。', 'Có vẻ sẽ có mưa rào nên tôi mang ô đi.',42, '/Sound/N3/1010.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '突然', 'とつぜん', 'Bỗng nhiên', '突然（とつぜん）、空（そら）が暗（くら）くなった。', 'Bỗng nhiên trời tối.',42, '/Sound/N3/1011.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'とたん[に]', 'Không có Furigana', 'Vừa mới', '家（いえ）に帰（かえ）ると、とたんに雨（あめ）が降（ふ）ってきた。', 'Vừa mới về đến nhà thì trời đổ mưa.',42, '/Sound/N3/1012.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ぬれる', 'Không có Furigana', 'Bị ướt, ướt', '傘（かさ）がなくて、雨（あめ）にぬれた。', 'Không có ô, bị mưa ướt.',42, '/Sound/N3/1013.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'あっという間', 'あっというま', 'Chẳng mấy chốc, loáng một cái', '雨（あめ）はあっという間に、はげしくなった。', 'Mưa chẳng mấy chốc trở nên dữ dội.',42, '/Sound/N3/1014.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '止む', 'やむ', 'Tạnh, ngớt', 'にわか雨なので、すぐに止（や）むだろう。', 'Mưa rào nên chắc là sẽ tạnh ngay thôi.',42, '/Sound/N3/1015.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ところどころ', 'Không có Furigana', 'Chỗ này chỗ kia', '昨日（きのう）降（ふ）った雪（ゆき）が、ところどころにのこっている。', 'Tuyết rơi ngày hôm qua vẫn còn ở chỗ này chỗ kia.',42, '/Sound/N3/1016.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '積もる', 'つもる', 'Tích tụ, chất đống, phủ', '雪（ゆき）が積（つ）もっているので、気（き）をつけて歩（ある）こう。', 'Tuyết phủ đầy nên đi cẩn thận nhé!',42, '/Sound/N3/1017.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '快晴', 'かいせい', 'Trời trong xanh', '今日（きょう）は快晴（かいせい）で、雲（くも）が一（ひと）つもない。', 'Hôm nay trời trong xanh, không có một đám mây nào.',42, '/Sound/N3/1018.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'かがやく', 'Không có Furigana', 'Sáng lung linh, sáng lấp lánh', '今夜（こんや）は、月（つき）がかがやいている。', 'Đêm nay, trang sáng lung linh.',42, '/Sound/N3/1019.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'まぶしい', 'Không có Furigana', 'Chói, chói lóa', '急（きゅう）に天気（てんき）がよくなって、太陽（たいよう）がまぶしい。', 'Đột nhiên trời nắng, mặt trời chói chang.',42, '/Sound/N3/1020.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'のち', 'Không có Furigana', 'Sau', '明日（あした）の天気（てんき）は、雨（あめ）のち晴（は）れだそうだ。', 'Thời tiết ngày mai sau mưa trời nắng.',42, '/Sound/N3/1021.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '当たる', 'あたる', 'Trúng, đúng', 'この番組（ばんぐみ）の天気（てんき）予報（よほう）は、よく当（あ）たる。', 'Dự báo thời tiết của chương trình này hay đúng.',42, '/Sound/N3/1022.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ふるさと', 'Không có Furigana', 'Quê, quê hương', '私（わたし）のふるさとでは、雪（ゆき）がたくさん降（ふ）る。', 'Quê tôi tuyết rơi nhiều.',42, '/Sound/N3/1023.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '蒸し暑い', 'むしあつい', 'Nóng ẩm, oi bức', '今日（きょう）は湿度（しつど）が高（たか）くて、蒸し暑（むしあつ）い。', 'Hôm nay độ ẩm cao, trời oi bức.',43, '/Sound/N3/1024.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '温度計', 'おんどけい', 'Nhiệt kế', 'この温度（おんど）計（けい）は湿度（しつど）も測（はか）れる。', 'Nhiệt kế này có thể đo cả độ ẩm.',43, '/Sound/N3/1025.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'プラス', 'Không có Furigana', 'Dương, tăng', '明日（あした）の気温（きおん）は、今日（きょう）と比（くら）べてプラス3度（ど）らしい。', 'Nghe nói nhiệt độ ngày mai tăng 3 độ so với hôm nay.',43, '/Sound/N3/1026.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'マイナス', 'Không có Furigana', 'Âm, giảm', '①今日（きょう）の気温（きおん）は、昨日（きのう）と比（くら）べてマイナス10たった。
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '凍る', 'こおる', 'Đóng băng', '道（みち）が凍（こお）っていて、とても危（あぶ）ない。', 'Đường đóng băng, rất nguy hiểm.',43, '/Sound/N3/1028.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '氷', 'こおり', 'Băng, đá', '夏（なつ）、冷凍庫（れいとうこ）で氷（こおり）を作（つく）っておく。', 'Mùa hè, tôi làm sẵn đá bằng tủ đông.',43, '/Sound/N3/1029.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '冷える', 'ひえる', 'Lạnh, cóng', '天気（てんき）予報（よほう）によると、夕方（ゆうがた）から冷（ひ）えるらしい。', 'Theo dự báo thời tiết, từ chiều tối trời sẽ lạnh.',43, '/Sound/N3/1030.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'けっこう', 'Không có Furigana', 'Khá', '今週（こんしゅう）、けっこう寒（さむ）い日（ひ）が続（つづ）いている。', 'Tuần này, những ngày khá lạnh sẽ kéo dài.',43, '/Sound/N3/1031.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '非常な', 'ひじょうな', 'Rất', '今年（ことし）の夏（なつ）は、非常（ひじょう）に暑（あつ）くなりそうだ。', 'Mùa hè năm nay có vẻ sẽ rất nóng.',43, '/Sound/N3/1032.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '夏日', 'なつび', 'Ngày hè nhiệt độ trên 25 độ', '今日（きょう）は、今年（ことし）初（はじ）めての夏（なつ）日（び）になった。', 'Hôm nay là ngày hè đầu tiên trong năm nhiệt độ trên 25 độ.',43, '/Sound/N3/1033.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '真夏日', 'まなつび', 'Ngày hè nóng trên 30 độ', '今年（ことし）は、真夏（まなつ）日（び）が多（おお）いそうだ。', 'Nghe nói năm nay những ngày hè nóng trên 30 độ nhiều.',43, '/Sound/N3/1034.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '猛暑日', 'もうしょび', 'Ngày hè cực nóng trên 35 độ', '猛暑（もうしょ）日（び）が続（つづ）いているので、体（からだ）に気（き）をつけている。', 'Những ngày hè cực nóng trên 35 độ kéo dài nên chú ý giữ gìn sức khỏe.',43, '/Sound/N3/1035.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '冬日', 'ふゆび', 'Ngày đông nhiệt độ thấp nhất dưới âm', '冬日（ふゆび）には、手袋（てぶくろ）もマフラーを必要（ひつよう）だ。', 'Trong ngày đông nhiệt độ thấp nhất dưới 0 độ cần cả găng tay và khăn quàng cổ.',43, '/Sound/N3/1036.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '真冬日', 'まふゆび', 'Ngày đông hàn nhiệt độ cao nhất dưới âm', '東京（とうきょう）で、真冬（まふゆ）日（び）はとても珍（めずら）しい。', 'Ở Tokyo rất hiếm những ngày đông nhiệt độ cao nhất dưới 0 độ.',43, '/Sound/N3/1037.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '暖冬', 'だんとう', 'Mùa đông ấm', '今年（ことし）は暖冬（だんとう）で、雪（ゆき）が少（すく）ない。', 'Năm nay mùa đông ấm, tuyết rơi ít.',43, '/Sound/N3/1038.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '冷夏', 'れいか', 'Mùa hè mát', '冷夏（れいか）になると、海（うみ）に行（い）く人（ひと）が減（へ）る。', 'Khi mùa hè mát hơn mọi năm, những người đi biển giảm.',43, '/Sound/N3/1039.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'せっかく', 'Không có Furigana', 'Chẳng mấy khi, được', 'せっかくの休（やす）みなのに、天気（てんき）が悪（わる）くて寒（さむ）い。', 'Được ngày nghỉ mà thời tiết vừa xấu vừa lạnh.',43, '/Sound/N3/1040.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '状態', 'じょうたい', 'Trạng thái, tình trạng', '大雨（おおあめ）で、この川（かわ）は危険（きけん）な状態（じょうたい）だ。', 'Trời mưa lớn, con sông này trong tình trạng nguy hiểm.',44, '/Sound/N3/1041.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '変化＜する＞', 'へんか＜する＞', 'Sự thay đổi', '旅行（りょこう）中（ちゅう）、天気（てんき）の変化（へんか）がとても気（き）になる。', 'Trong thời gian du lịch tô rất bận tâm về sự thay đổi của thời tiết.',44, '/Sound/N3/1042.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '一定＜する＞', 'いってい＜する＞', 'Sự ổn định', '最近（さいきん）、気温（きおん）が一定（いってい）している。', 'Gần đây nhiệt độ ổn định.',44, '/Sound/N3/1043.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '観察＜する＞', 'かんさつ＜する＞', 'Sự quan sát', '弟（おとうと）とは、雲（くも）の形（かたち）を観察（かんさつ）している。', 'Em trai tôi đang quan sát hình dáng đám mây.',44, '/Sound/N3/1044.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '次第に', 'しだいに', 'Dần dần', '今日（きょう）の夜（よる）から、次第（しだい）に天気（てんき）が悪（わる）くなるらしい。', 'Có vẻ từ đêm nay thời tiết sẽ xấu dần.',44, '/Sound/N3/1045.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'じょじょに', 'Không có Furigana', 'Dần dần, từ từ', '朝（あさ）から、気温（きおん）がじょじょに上（あ）がってきた。', 'Từ sáng, nhiệt độ từ từ tăng.',44, '/Sound/N3/1046.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'だんだん[と]', 'Không có Furigana', 'Dần dần', '空（そら）がだんだん暗（くら）くなってきた。', 'Trời dần dần tối.',44, '/Sound/N3/1047.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ますます', 'Không có Furigana', 'Càng ngày càng, càng hơn', '今週（こんしゅう）になって、ますます暑（あつ）くなってきた。', 'Sang tuần này, trời càng nóng hơn.',44, '/Sound/N3/1048.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'すっかり', 'Không có Furigana', 'Hẳn, thực sự, hoàn toàn', '最近（さいきん）、暖（あたた）かい日（ひ）が多（おお）い。すっかり春（はる）だ。', 'Gần đây những ngày ấm áp nhiều. Mùa xuân thực sự đã đến rồi.',44, '/Sound/N3/1049.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '一気に', 'いっきに', 'Bỗng, một lèo', '12月（がつ）になって、一気（いっき）に寒（さむ）くなった。', 'Sang tháng 12, trời bỗng đổ lạnh.',44, '/Sound/N3/1050.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '一度に', 'いちどに', 'Cùng một lúc, đồng loạt', '今日（きょう）は、夏（なつ）と冬（ふゆ）が一（いち）度（ど）に来（き）たようだった。', 'Ngày hôm nay cứ như là mùa hè với mùa đông đến cùng một lúc vậy.',44, '/Sound/N3/1051.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いっぺんに', 'Không có Furigana', 'Cùng một lúc, đồng loạt', '2つの台風（たいふう）がいっぺんに来（き）た。', '2 cơn bão đến cũng một lúc.',44, '/Sound/N3/1052.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いつの間にか', 'いつのまにか', 'Lúc nào không hay', '雪（ゆき）がいつの間にか止（や）んでいた。', 'Tuyết ngừng rơi lúc nào không hay.',44, '/Sound/N3/1053.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '温暖化', 'おんだんか', 'Hiện tượng trái đất ấm dần', '世界中（せかいじゅう）で、温暖（おんだん）化（か）が進（すす）んでいるようだ。', 'Hiện tượng trái đất ấm dần dường như đang diễn ra trên khắp thế giới.',44, '/Sound/N3/1054.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'えいきょう＜する＞', 'Không có Furigana', 'Sự ảnh hưởng', '温暖（おんだん）化（か）は、いろいろなえいきょうを与（あた）えている。', 'Hiện tượng trái đất ấm dần lên đang gây nhiều ảnh hưởng.',44, '/Sound/N3/1055.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '変な', 'へんな', 'Lạ, bất thường', '最近（さいきん）ずっと、変（へん）な天気（てんき）が続（つづ）いている。', 'Gần đây thời tiết bất thường kéo dài suốt.',44, '/Sound/N3/1056.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '祝日', 'しゅくじつ', 'Ngày nghỉ lễ', '日本（にほん）の祝日（しゅくじつ）を、カレンダーで確認（かくにん）する。', 'Tôi xác nhận ngày nghỉ lễ của Nhật Bản qua lịch.',45, '/Sound/N3/1057.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '年末年始', 'ねんまつねんし', 'Đầu năm cuối năm', '年末年始（ねんまつねんし）に海外（かいがい）旅行（りょこう）をする人（ひと）が多（おお）い。', 'Vào dịp đầu năm cuối năm nhiều người đi du lịch nước ngoài.',45, '/Sound/N3/1058.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '元日', 'がんじつ', 'Ngày mùng một tết', '元日（がんじつ）に友（とも）だちと神社（じんじゃ）に行（い）った。', 'Ngày mùng một tết, tôi cùng bạn đến đền thờ Thần đạo.',45, '/Sound/N3/1059.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '迎える', 'むかえる', 'Đón', '来年（らいねん）は、富士山（ふじさん）で新年（しんねん）を迎（むか）えたい。', 'Sang năm, tôi muốn đón năm mới trên núi Phú Sỹ.',45, '/Sound/N3/1060.mp3');
