INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '成人の日', 'せいじんのひ', 'Ngày lễ trưởng thành', '成人の日（せいじんのひ）に、20歳（はたち）の若者（わかもの）が会場（かいじょう）に集（あつ）まった。', 'Vào ngày lễ trưởng thành, các nam thanh niên 20 tuổi đã tập trung ở hội trường.',45, '/Sound/N3/1063.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ひな祭り', 'ひなまつり', 'Ngày lễ búp bê Hina', 'ひな祭りは女の子（おんなのこ）のお祝いだ。', 'Ngày lễ búp bê Hina là ngày lễ mừng cho các bé gái.',45, '/Sound/N3/1064.mp3');
