INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'もともと', 'Không có Furigana', 'Vốn dĩ', '体育（たいいく）の日（ひ）は、もともと10月（がつ）10日（にち）だった。', 'Ngày thể dục thể thao vốn dĩ là ngày mùng 10 tháng 10.',45, '/Sound/N3/1072.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '七五三', 'しちごさん', 'Ngày lễ Shichi-go-san', '七（なな）五（ご）三（さん）のお祝いに、神社（じんじゃ）で記念（きねん）写真（しゃしん）を撮（と）る。', 'Chụp ảnh kỷ niệm ở đền thờ Thần đạo mừng ngày lễ Shichi-go-san.',45, '/Sound/N3/1073.mp3');
