INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '犯罪', 'はんざい', 'Tội phạm', 'あの町（まち）は犯罪（はんざい）が多（おお）い。', 'Thành phố đó có nhiều tội phạm.',47, '/Sound/N3/1103.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '発見者', 'はっけんしゃ', 'Người phát hiện', '警察（けいさつ）は、怪（あや）しいかばんの発見（はっけん）者（しゃ）に話（はなし）を聞（き）いている。', 'Cảnh sát hỏi chuyện người phát hiện ra chiếc cặp khả nghi.',47, '/Sound/N3/1104.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '疑う', 'うたがう', 'Nghi ngờ', '警察（けいさつ）に、第（だい）一（いち）発見（はっけん）者（しゃ）が疑（うたが）われているようだ。', 'Dường như người phát hiện đầu tiên bị cảnh sát nghi ngờ.',47, '/Sound/N3/1105.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'うそつき', 'Không có Furigana', 'Kẻ nói dối, sự nói dối', 'だれも彼（かれ）をうそつきとは思（おも）わなかった。', 'Không ai nghĩ anh ta là kẻ nói dối.',47, '/Sound/N3/1106.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '犯人', 'はんにん', 'Phạm nhân, thủ phạm', '犯人（はんにん）は、20歳（さい）くらいの男（おとこ）らしい。', 'Nghe nói thủ phạm là nam giới khoảng 20 tuổi.',47, '/Sound/N3/1107.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いたずら＜な/する＞', 'Không có Furigana', 'Trò nghịch ngợm (nghịch ngợm)', '子（こ）どものいたずらが、大（おお）きな事件（じけん）になった。
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'さけぶ', 'Không có Furigana', 'Gào, thét', '外（そと）で女性（じょせい）が大（おお）きな声（こえ）でさけんでいる。', 'Người phụ nữ đang gào thét to ở bên ngoài.',47, '/Sound/N3/1109.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'たたく', 'Không có Furigana', 'Gõ', 'だれかがドアをたたいている。', 'Có ai đó đang gõ cửa.',47, '/Sound/N3/1110.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '盗む', 'ぬすむ', 'Lấy trộm', '泥棒（どろぼう）に指輪（ゆびわ）を盗（ぬす）まれた。', 'Tôi bị kẻ trộm lấy trộm chiếc nhẫn.',47, '/Sound/N3/1111.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'うばう', 'Không có Furigana', 'Cướp', '道（みち）を歩（ある）いていて、ハンドバッグをうばわれた。', 'Đang đi đường thì bị cướp túi xách.',47, '/Sound/N3/1112.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '捜す', 'さがす', 'Tìm', '警察（けいさつ）が犯人（はんにん）を捜（さが）している。', 'Cảnh sát đang truy tìm thủ phạm.',47, '/Sound/N3/1113.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '追う', 'おう', 'Đuổi theo', '警察（けいさつ）が犯人（はんにん）を追（お）っている。', 'Cảnh sát đang đuổi theo thủ phạm.',47, '/Sound/N3/1114.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '捕まえる', 'つかまえる', 'Bắt, tóm', '警察（けいさつ）が犯人（はんにん）をやっと捕（つか）まえた。', 'Cuối cùng cảnh sát cũng đã bắt được đối tượng.',47, '/Sound/N3/1115.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '捕まる', 'つかまる', 'Bị bắt', '家（いえ）の近（ちか）くで、犯人（はんにん）が捕（つか）まった。', 'Thủ phạm bị bắt gần nhà tôi.',47, '/Sound/N3/1116.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '逮捕＜する＞', 'たいほ＜する＞', 'Bị bắt', '犯人（はんにん）が逮捕（たいほ）されて、市民（しみん）は安心（あんしん）した。', 'Tên thủ phạm đã bị bắt khiến người dân an tâm.',47, '/Sound/N3/1117.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '気味が悪い', 'きみがわるい', 'Cảm thấy ghê ghê, cảm thấy sợ sợ', '知（し）らない人（ひと）から電話（でんわ）がかかってきて、気味が悪（きみがわる）い。', 'Có người lạ gọi điện đến, cảm thấy sợ sợ.',47, '/Sound/N3/1118.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'パトカー', 'Không có Furigana', 'Xe cảnh sát', '昨日（きのう）、深夜（しんや）までパトカーの音（おと）が聞（き）こえた。', 'Hôm qua, tôi nghe thấy tiếng xe cảnh sát đến tận nửa đêm.',47, '/Sound/N3/1119.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '（事故に）あう', '（じこに）あう', 'Gặp (tai nạn)', '子（こ）どものころ、交通（こうつう）事故（じこ）にあったことがある。', 'Hồi bé, tôi từng bị gặp tai nạn giao thông.',48, '/Sound/N3/1120.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '発生＜する＞', 'はっせい＜する＞', 'Sự phát sinh, xảy ra', 'あの交差点（こうさてん）では、毎日（まいにち）事故（じこ）が発生（はっせい）している。', 'Ở giao lộ ấy tai nạn xảy ra hàng ngày.',48, '/Sound/N3/1121.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '命', 'いのち', 'Sinh mạng', '命（いのち）は何（なに）よりも大切（たいせつ）だ。', 'Sinh mạng quan trọng hơn bất cứ thứ gì.',48, '/Sound/N3/1122.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '救う', 'すくう', 'Cứu', '知（し）らない人（ひと）に、命（いのち）を救（すく）ってもらった。', 'Tôi được một người không quen biết cứu mạng.',48, '/Sound/N3/1123.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'そうぞうしい', 'Không có Furigana', 'Ồn ào', '信号（しんごう）の近（ちか）くが、ずいぶんそうぞうしい。', 'Gần đèn xanh đèn đỏ quá là ồn ào.',48, '/Sound/N3/1124.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'さわぐ', 'Không có Furigana', 'Làm ầm ĩ, kêu ầm ĩ', '交差点（こうさてん）でさわいでいる人（ひと）がいる。', 'Có người đang kêu ầm ĩ ở giao lộ.',48, '/Sound/N3/1125.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '現場', 'げんば', 'Hiện trường, chỗ đó', '大（おお）きな音（おと）がしたので、現場（げんば）に行（い）ってみた。', 'Có tiếng động lớn nên tôi thử ra chỗ đó coi.',48, '/Sound/N3/1126.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '混乱＜する＞', 'こんらん＜する＞', 'Sự hỗn loạn, náo loạn', '現場（げんば）は、警察（けいさつ）と多（おお）くの人（ひと）で混乱（こんらん）している。', 'Hiện trường đang náo loạn bởi cảnh sát và nhiều người.',48, '/Sound/N3/1127.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'パニック', 'Không có Furigana', 'Hoảng loạn', '大（おお）きな事故（じこ）の現場（げんば）を見（み）て、パニックになった。', 'Nhìn hiện trường vụ tai nạn lớn mà bị hoảng loạn.',48, '/Sound/N3/1128.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '無事＜な＞', 'ぶじ＜な＞', 'Sự an toàn, bình an vô sự (an toàn, bình an)', '事故（じこ）にあった人（ひと）の無事（ぶじ）が確認（かくにん）された。（名）
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '防ぐ', 'ふせぐ', 'Phòng, đề phòng', '警察（けいさつ）は、犯罪（はんざい）を防（ふせ）ぐことができなかった。', 'Cảnh sát đã không thể đề phòng được tội phạm.',48, '/Sound/N3/1130.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '再び', 'ふたたび', 'Lại, một lần nữa lại', 'ひどい事故（じこ）が再（ふたた）び起（お）きてしまった。', 'Vụ tai nạn thảm khốc một lần nữa lại xảy ra.',48, '/Sound/N3/1131.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'わざと', 'Không có Furigana', 'Cố tình', '運転（うんてん）手（しゅ）は、わざと事故（じこ）を起（お）こしたのかもしれない。', 'Có thể người lái xe đã cố tình để xảy ra tai nạn.',48, '/Sound/N3/1132.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '被害者', 'ひがいしゃ', 'Người bị nạn, nạn nhân', '事故（じこ）の被害（ひがい）者（しゃ）は、大（おお）けがをしたらしい。', 'Nghe nói nạn nhân trong vụ tai nạn bị thương nặng.',48, '/Sound/N3/1133.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '[お]互いに', '[お]たがいに', 'Nhau, cùng nhau', '自転車（じてんしゃ）に乗（の）っている人（ひと）も、歩（ある）いている人（ひと）も互（たが）いに注意（ちゅうい）が必要（ひつよう）だ。', 'Cả người đi xe đạp lẫn người đi bộ cần chú ý nhau.',48, '/Sound/N3/1134.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '疑問', 'ぎもん', 'Nghi vấn', 'この事故（じこ）には、いくつかの疑問（ぎもん）がある。', 'Vụ tai nạn này có một vài nghi vấn.',48, '/Sound/N3/1135.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '飛び込む', 'とびこむ', 'Nhảy xuống, nhảy vào', '準備（じゅんび）運動（うんどう）をしないで、海（うみ）に飛び込（とびこ）んではいけない。', 'Không được nhảy xuống biển mà không khởi động trước.',48, '/Sound/N3/1136.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'おぼれる', 'Không có Furigana', 'Đuối nước', '一人（ひとり）の男性（だんせい）が、お酒（さけ）を飲（の）んで泳（およ）いでおぼれた。', 'Một người đàn ông bị đuối nước do uống rượu rồi bơi.',48, '/Sound/N3/1137.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '飛び出す', 'とびだす', 'Phi ra, lao ra', '子（こ）どもが道（みち）に、飛び出（とびだ）さないようにしてください。', 'Đừng để trẻ con lao ra đường!',48, '/Sound/N3/1138.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '行方不明', 'ゆくえふめい', 'Mất tích', '山（やま）で、3人（にん）の男女（だんじょ）が行方（ゆくえ）不明（ふめい）になっている。', 'Có ba người nam nữ mất tích trên núi.',48, '/Sound/N3/1139.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '亡くなる', 'なくなる', 'Mất, chết', '毎年（まいとし）、登山（とざん）中（ちゅう）に亡（な）くなる人（ひと）がいる。', 'Hàng năm có người chết khi đang leo núi.',48, '/Sound/N3/1140.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '偶然', 'ぐうぜん', 'Ngẫu nhiên, tình cờ', 'それは偶然（ぐうぜん）の事故（じこ）だった。
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '苦情', 'くじょう', 'Kêu ca, phàn nàn', '近所（きんじょ）から苦情（くじょう）があったので、謝（あやま）った。', 'Bị hàng xóm kêu ca, tôi đã xin lỗi.',49, '/Sound/N3/1142.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'あわただしい', 'Không có Furigana', 'Tất bật', 'ゆうべ社長（しゃちょう）が亡（な）くなり、今日（きょう）はーち日（ひ）あわただしい。', 'Giám đốc đột ngột mất tối qua, hôm nay tất bật cả 1 ngày.',49, '/Sound/N3/1143.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'あわてる', 'Không có Furigana', 'Cuống lên', 'バッグの中（なか）に財布（さいふ）が見（み）つからず、あわてた。', 'Tôi đã cuống lên vì không nhìn thấy ví trong túi đâu.',49, '/Sound/N3/1144.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いきなり', 'Không có Furigana', 'Bỗng nhiên', 'アパートの前（まえ）で、いきなり名前（なまえ）を呼（よ）ばれた。', 'Tôi bỗng nhiên bị gọi tên trước căn hộ.',49, '/Sound/N3/1145.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いじめ', 'Không có Furigana', 'Sự trêu chọc, bắt nạt', 'いじめは社会（しゃかい）の問題（もんだい）だ。', 'Bắt nạt là một vấn đề của xã hội.',49, '/Sound/N3/1146.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'いじめる', 'Không có Furigana', 'Trêu chọc, bắt nạt', '友（とも）だちをいじめるなんて、最低（さいてい）だ。', 'Bắt nạt bạn bè thật là tệ.',49, '/Sound/N3/1147.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '迷子', 'まいご', 'Trẻ lạc, sự đi lạc', '日本（にほん）へ来（き）たころ、よく迷子（まいご）になった。', 'Hồi đến Nhật, tôi hay bị lạc đường.',49, '/Sound/N3/1148.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '場合', 'ばあい', 'Trường hợp', '迷子（まいご）になった場合（ばあい）は、交番（こうばん）で聞（き）いてください。', 'Trường hợp bị lạc, hãy đến đồn cản sát hỏi đường.',49, '/Sound/N3/1149.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '落とす', 'おとす', 'Đánh rơi', '駅（えき）で定期（ていき）券（けん）を落（お）としたが、戻（もど）ってきた。', 'Tôi đánh rơi vé tháng ở ga nhưng đã được trả lại.',49, '/Sound/N3/1150.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'なくす', 'Không có Furigana', 'Đánh mất, làm mất', '大切（たいせつ）な書類（しょるい）をなくして、部長（ぶちょう）にひどく叱（しか）られた。', 'Tôi làm mất giấy tờ quan trọng, bị trưởng phòng mắng thậm tệ.',49, '/Sound/N3/1151.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '借金くする>', 'しゃっきん＜する＞', 'Sự vay tiền, tiền nợ', '親（おや）に借金（しゃっきん）をしたが、まだ半分（はんぶん）も返（かえ）していない。', 'Tôi vay tiền của bố mẹ nhưng vẫn chưa trả được một nửa.',49, '/Sound/N3/1152.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ずるい', 'Không có Furigana', 'Gian dối, khôn lỏi', '彼（かれ）はずるい人（ひと）だから、信（しん）じないほうがいい。', 'Anh ta là một người gian dối, vì vậy không nên tin.',49, '/Sound/N3/1153.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '倒れる', 'たおれる', 'Đổ, đổ bệnh', '①地震（じしん）でビルが倒（たお）れた。
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '転ぶ', 'ころぶ', 'Bị ngã', '雪（ゆき）の日（ひ）に転（ころ）んで、足（あし）の骨（ほね）を折（お）った。', 'Vào hôm tuyết rơi, tôi bị ngã gãy xương chân.',49, '/Sound/N3/1155.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '（会社が）つぶれる', '（かいしゃが）つぶれる', '(Công ty) bị phá sản, bị dập', '①箱（ばこ）を落（お）として、ケーキがつぶれてしまった。
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '次々[と]', 'つぎつぎ [と]', 'Liên tiếp', '会社（かいしゃ）で、次々（つぎつぎ）と問題（もんだい）が発生（はっせい）した。', 'Trong công ty các vấn đề liên tiếp phát sinh.',49, '/Sound/N3/1157.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '停電＜する＞', 'ていでん＜する＞', 'Sự mất điện, cúp điện', '地域（ちいき）で停電（ていでん）があり、2時間（じかん）も回復（かいふく）しなかった。', 'Tại khu vực đã xảy ra tình trạng mất điện và mãi không phục hồi được trong vòng hai tiếng.',49, '/Sound/N3/1158.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '断水＜する＞', 'だんすい＜する＞', 'Sự mất nước, cúp nước', 'この地域（ちいき）は地震（じしん）で断水（だんすい）した。', 'Khu vực này bị mất nước do động đất.',49, '/Sound/N3/1159.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ゆれる', 'Không có Furigana', 'Rung lắc', 'このマンションは、地震（じしん）のとき、けっこうゆれる。', 'Tòa nhà chung cư này bị rung lắc khá mạnh khi động đất.',49, '/Sound/N3/1160.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ぐらぐら＜する＞', 'Không có Furigana', 'Bần bật', '地震（じしん）で家具（かぐ）がぐらぐら揺（ゆ）れた。', 'Đồ đạc trong nhà rung bần bật vì động đất.',49, '/Sound/N3/1161.mp3');
