INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ユーモア', 'Không có Furigana', 'Hài hước', 'ユーモアのない人（ひと）と話（はな）しても、おもしろくない。', 'Nói chuyện với người không có tính hài hước chẳng thú vị.',52, '/Sound/N3/1221.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ゆかいな', 'Không có Furigana', 'Vui vẻ, dễ chịu', '彼（かれ）はゆかいな人（ひと）だ。', 'Anh ta là người vui vẻ dễ chịu.',52, '/Sound/N3/1222.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '愛する', 'あいする', 'Yêu', 'あの歌手（かしゅ）は世界中（せかいじゅう）で愛（あい）されている。', 'Ca sỹ đó được cả thế giới yêu mến.',52, '/Sound/N3/1223.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '真剣な', 'しんけんな', 'Nghiêm túc', '彼（かれ）は彼女（かのじょ）を真剣（しんけん）に愛（あい）しているらしい。', 'Dường như anh ta yêu cô ấy nghiêm túc.',52, '/Sound/N3/1224.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'どきどき＜する＞', 'Không có Furigana', 'Tim đập rộn ràng, xốn xang, hồi hộp', '大好（だいす）きな人（ひと）が近（ちか）くにいると、どきどきする。', 'Hễ có người mình thích ở bên cạnh là tim tôi đập rộn ràng.',52, '/Sound/N3/1225.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'わくわく＜する＞', 'Không có Furigana', 'Sự phấn chấn, hồi hộp', '来週（らいしゅう）、日本（にほん）に留学（りゅうがく）するので、わくわくしている。', 'Tuần sau tôi đi du học ở Nhật Bản nên cảm thấy hồi hộp.',52, '/Sound/N3/1226.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ほっと＜する＞', 'Không có Furigana', 'Sự an tâm, nhẹ người', '試験（しけん）に合格（ごうかく）して、ほっとした。', 'Cảm thấy  nhẹ nhõm khi biết mình thi đỗ.',52, '/Sound/N3/1227.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'のんびり＜する＞', 'Không có Furigana', 'Sự thong thả, ung dung', '将来（しょうらい）は、いなかでのんびり暮（く）らしたい。', 'Trong tương lai, tôi muốn sống thong thả ở chốn quê.',52, '/Sound/N3/1228.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ほほえむ', 'Không có Furigana', 'Mỉm cười', '彼女（かのじょ）がほほえむと、みんなが幸（しあわ）せな気持（きも）ちになる。', 'Khi cô ấy mỉm cười, mọi người đều cảm thấy hạnh phúc.',52, '/Sound/N3/1229.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '夢中＜な＞', 'むちゅう＜な＞', 'Sự say mê, say sưa', '妹（いもうと）はおしゃれに夢中（むちゅう）だ。（ナ形）', 'Em gái tôi say sưa ăn diện.',52, '/Sound/N3/1230.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '勇気', 'ゆうき', 'Lòng dũng cảm', '勇気（ゆうき）を持（も）って、チャレンジしよう。', 'Hãy dũng cảm thử sức nào!',52, '/Sound/N3/1231.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'あきる', 'Không có Furigana', 'Chán', 'このドラマには、もうあきてしまった。', 'Tôi đã chán bộ phim này rồi.',53, '/Sound/N3/1232.mp3');
