INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'がっかり＜する＞', 'Không có Furigana', 'Sự thất vọng', '試験（しけん）の結果（けっか）が悪（わる）くて、がっかりした。', 'Tôi thất vọng vì kết quả kỳ thi kém.',53, '/Sound/N3/1235.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '悲しむ', 'かなしむ', 'Đau buồn', '大（おお）きな事故（じこ）が起（お）きて、国（くに）中（じゅう）が悲（かな）しんでいる。', 'Tai nạn lớn xảy ra, cả nước đau buồn.',53, '/Sound/N3/1236.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'かわいそうな', 'Không có Furigana', 'Đáng thương, tội nghiệp', 'この地域（ちいき）の子（こ）どもたちは学校（がっこう）に行（い）けない。かわいそうだ。', 'Bọ trẻ con ở khu vực này không được đến trường. Thật đáng thương.',53, '/Sound/N3/1237.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '気の毒な', 'きのどくな', 'Tội nghiệp, kém may mắn', '気の毒（きのどく）な人（ひと）たちに、元気（げんき）を与（あた）えたい。', 'Tôi muốn mang lại niềm vui cho những con người tội nghiệp.',53, '/Sound/N3/1238.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'キつい', 'Không có Furigana', 'Chật, vất vả, cực nhọc', '①このくつはきつくて、はけない。
