INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '後悔＜する＞', 'こうかい＜する＞', 'Sự ân hận', '高（たか）いバッグを買（か）って、後悔（こうかい）している。', 'Tôi đang ân hận vì mua cái túi đắt tiền.',53, '/Sound/N3/1242.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '悩む', 'なやむ', 'Trăn trở', '一人（ひとり）で悩（なや）んでいないで、話（はなし）を聞（き）かせてください。', 'Đừng trăn trở một mình nữa mà hãy nói cho tôi nghe!',53, '/Sound/N3/1243.mp3');
