INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ぶつぶつ', 'Không có Furigana', 'Lầm bầm', '彼（かれ）は、いつもぶつぶつ言（い）っている。', 'Anh ta lúc nào cũng lầm bầm.',53, '/Sound/N3/1249.mp3');
