INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '願う', 'ねがう', 'Cầu mong, cầu nguyện', '合格（ごうかく）を願（ねが）って、有名（ゆうめい）な神社（じんじゃ）に行（い）った。', 'Tôi đến ngôi đền nổi tiếng để cầu mong thi đỗ.',54, '/Sound/N3/1255.mp3');
