INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '感じる', 'かんじる', 'Cảm nhận', '彼女（かのじょ）の言葉（ことば）に愛情（あいじょう）を感（かん）じた。', 'Tôi cảm nhận được tình yêu trong câu nói của cô ấy.',54, '/Sound/N3/1257.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'あいまいな', 'Không có Furigana', 'Mập mờ, mơ hồ', '日本語（にほんご）には、あいまいな表現（ひょうげん）が多（おお）い。', 'Trong tiếng Nhật, cách nói mập mờ thì nhiều.',54, '/Sound/N3/1258.mp3');
