INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'なんとか', 'Không có Furigana', 'Cố hết sức, bằng cách nào đó', 'レポートを締め切（しめき）りになんとか間に合（まにあ）わせたい。', 'Tôi muốn cố hết sức để kịp thời hạn nộp báo cáo.',54, '/Sound/N3/1262.mp3');
