INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '表現＜する＞', 'ひょうげん＜する＞', 'Sự diễn đạt, thể hiện, cách nói', '私（わたし）は、気持（きも）ちをうまく表現（ひょうげん）できない。', 'Tôi không thể diễn đạt tốt tâm trạng của mình.',55, '/Sound/N3/1269.mp3');
