INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '平気＜な＞', 'へいき＜な＞', 'Sự bình thản (bình thản)', '彼（かれ）は平気（へいき）そうな顔（かお）をしているが、本当（ほんとう）の気持（きも）ちはわからない。（ナ形）', 'Anh ta trông mặt thì bình thản nhưng tâm trạng thực sự thì không biết thế nào.',55, '/Sound/N3/1277.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '本気＜な＞', 'ほんき＜な＞', 'Sự thật lòng (thật lòng)', 'その人（ひと）が本気（ほんき）かどうか、目（め）を見（み）ればわかる。（ナ形）', 'Con người ấy có thật lòng hay không nhìn vào mắt là biết.',55, '/Sound/N3/1278.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '迷う', 'まよう', 'Lạc', '映画館（えいがかん）に行（い）きたいが、道（みち）に迷（まよ）ってしまった。', 'Tôi muốn đi đến rạp chiếu phim nhưng đã bị lạc mất đường.',55, '/Sound/N3/1279.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '迷い', 'まよい', 'Phân vân', '留学（りゅうがく）したいと思（おも）っているが、気持（きも）ちに迷（まよ）いがある。', 'Tôi muốn đi du học nhưng trong lòng đang phân vân.',55, '/Sound/N3/1280.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '微妙な', 'びみょうな', 'Khó tả', 'うれしいのか、さびしいのか微妙（びみょう）な気持（きも）ちだ。', 'Tâm trạng khó tả không biết vui hay buồn.',55, '/Sound/N3/1281.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '魅力', 'みりょく', 'Vẻ hấp dẫn', '彼女（かのじょ）のきれいな目（め）に、魅力（みりょく）を感（かん）じた。', 'Tôi đã cảm nhận được vẻ hấp dẫn trong đôi mắt đẹp của cô ấy.',55, '/Sound/N3/1282.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '本音', 'ほんね', 'Suy nghĩ thực, lòng dạ', '日本人（にほんじん）は、なかなか本音（ほんね）を言（い）わない。', 'Người Nhật không hay nói suy nghĩ thực của mình.',55, '/Sound/N3/1283.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ましな', 'Không có Furigana', 'Còn tốt hơn', 'あんな人（ひと）の下（した）で働（はたら）くくらいなら、辞（や）めるほうがましだ。', 'Nếu làm việc dưới trướng của người đó thì thả bỏ việc còn tốt hơn.',55, '/Sound/N3/1284.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '涙', 'なみだ', 'Nước mắt', '家族（かぞく）を思い出（おもいだ）して、ときどき涙（なみだ）が出（で）る。', 'Thỉnh thoảng tôi chảy nước mắt vì nhớ gia đình.',55, '/Sound/N3/1285.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '憎む', 'にくむ', 'Hận, căm thù', '彼（かれ）を愛（あい）していたが、今（いま）は憎（にく）んでいる。', 'Tôi đã từng yêu anh ta nhưng bây giờ thì hận.',55, '/Sound/N3/1286.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'カウンセリング＜する＞', 'Không có Furigana', 'Sự tư vấn, tư vấn tâm lý', 'カウンセリングを受（う）けると、気持（きも）ちが楽（らく）になる。', 'Được tư vấn tâm lý, tâm trạng tôi trở nên nhẹ nhõm.',55, '/Sound/N3/1287.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '模様', 'もよう', 'Hoa văn, họa tiết', '今年（ことし）は、どんな模様（もよう）の服（ふく）がはやりですか。', 'Năm nay trang phục có họa tiết như thế nào thịnh hành nhỉ?',56, '/Sound/N3/1288.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '特徴', 'とくちょう', 'Đặc điểm, đặc trưng', '彼女（かのじょ）のデザインには、特徴（とくちょう）がある。', 'Có đặc trưng trong thiết kế của cô ấy.',56, '/Sound/N3/1289.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '特色', 'とくしょく', 'Điểm đặc sắc', '兄（あに）はいつも特色（とくしょく）のない服装（ふくそう）をしている。', 'Anh tôi lúc nào cũng dùng trang phục không có gì đặc sắc.',56, '/Sound/N3/1290.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '柄', 'がら', 'Hoa văn, họa tiết', 'このT（てぃー）シャツは10種類（じゅっしゅるい）の柄（がら）から選（えら）べます。', 'Loại áo phông này có 10 kiểu hoa văn bạn có thể lựa chọn.',56, '/Sound/N3/1291.mp3');
