INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '無地', 'むじ', 'Trơn, không có họa tiết', '無地（むじ）のシャツは、柄（がら）のスカートと合（あ）わせやすい。', 'Áo trơn dễ phối với váy có họa tiết.',56, '/Sound/N3/1300.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'シンプルな', 'Không có Furigana', 'Đơn giản, giản dị', '今日（きょう）はシンプルなファッションで出（で）かけよう。', 'Hôm nay, mình hãy ra đường với thời trang đơn giản!',56, '/Sound/N3/1301.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '真っ赤な', 'まっかな', 'Đỏ rực', '真っ赤（まっか）な服（ふく）を着（き）て、パーティーに行（い）った。', 'Tôi mặc bộ quần áo đỏ rực đi dự tiệc.',56, '/Sound/N3/1302.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( 'ばらばらな', 'Không có Furigana', 'Lung tung, rời rạc', 'このシャツのボタンは、色（いろ）もデザインもばらばらだ。', 'Nút áo sơ mi này kiểu cọ lẫn màu sắc lung tung cả lên.',56, '/Sound/N3/1303.mp3');
