INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '外見', 'がいけん', 'Ngoại hình, vẻ bề ngoài, mã', '彼（かれ）は外見（がいけん）はいいが、性格（せいかく）に問題（もんだい）が削（けず）そうだ。', 'Anh ta đẹp mã nhưng tính cách có vấn đề.',57, '/Sound/N3/1307.mp3');
