INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '表情', 'ひょうじょう', 'Biểu lộ cảm xúc, nét mặt', 'あの子（こ）は表情（ひょうじょう）が豊（ゆた）かだ。', 'Biểu lộ cảm xúc của đứa bé ấy rất phong phú.',57, '/Sound/N3/1309.mp3');
INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '姿', 'すがた', 'Dáng, bóng dáng', '今日（きょう）、田中（たなか）さんの姿（すがた）を見（み）ていない。', 'Hôm nay, tôi không thấy bóng dáng của anh Tanaka đâu.',57, '/Sound/N3/1310.mp3');
