INSERT INTO vocabulary ( kanji, furigana, meaning, example, example_meaning, lesson_id,sound_url) VALUES( '上品な', 'じょうひんな', 'Thanh lịch, nhã nhặn', 'この女優（じょゆう）は、言葉（ことば）づかいがとても上品（じょうひん）だ。', 'Nữ diễn viên này có cách ăn nói rất thanh lịch.',57, '/Sound/N3/1315.mp3');
